//=====================================================================================================================
// Преобразование текстовой строки из кодировки CP-1251 (кириллица Windows) в кодировку UTF-8
string toUTF8(const string &str)
{
    string result;
    result.reserve(str.size() * 2);

    unsigned char *p;
    for (string::const_iterator i = str.begin(); i < str.end(); ++i)
    {
        p = _win2utf + 2 * (size_t)((unsigned char)*i);
        result += *(p++);
        if (*p != 0)
            result += *(p++);
    }

    return result;
}




//=====================================================================================================================
// Преобразование текстовой строки из кодировки UTF-8 в кодировку CP-1251 (кириллица Windows)
string fromUTF8(const string &str)
{
    string result;
    result.reserve(str.size());

    for (string::const_iterator i = str.begin(); i != str.end(); ++i)
    {
        // Обработка обычного символа ASCII
        if ((unsigned char)*i <= 0x7F)
            result += *i;

        // Обработка символов кириллицы
        else if ((unsigned char)*i == 0xD0)
        {
            ++i;
            if(i != str.end())
                result += (char)_utf2win0[(unsigned char)*i];
        }
        else if ((unsigned char)*i == 0xD1)
        {
            ++i;
            if (i != str.end())
                result += (char)_utf2win1[(unsigned char)*i];
        }

        // Пропуск прочих символов
    }

    return result;
}

